<?php session_start(); if (!isset($_SESSION['uniqueid'])) { 		echo "Invalid user"; 		session_destroy(); 		exit; } ?> <html> <head><script src='https://unpkg.com/tesseract.js@v2.0.2/dist/tesseract.min.js'></script>   <title>Generate bill</title>   <link rel="stylesheet" type="text/css" href="stylesheet_parkingLot.css">   <style>   body   {     background-image: url("https://www.muralswallpaper.co.uk/app/uploads/mint-green-geometric-wallpaper-mural-Room-1024x664.jpg");     background-repeat: no-repeat;     background-size: cover;   }   </style> </head> <body> <script>  function read_text() { 	 		var fullPath = document.getElementById('myfile').value; 	if (fullPath) 	{     		var startIndex = (fullPath.indexOf('\\') >= 0 ? fullPath.lastIndexOf('\\') : fullPath.lastIndexOf('/'));     		var filename = fullPath.substring(startIndex);     		if (filename.indexOf('\\') === 0 || filename.indexOf('/') === 0) 		{         			filename = filename.substring(1);     		}      	}         	Tesseract.recognize	(filename,'eng',{ logger: m => console.log(m) }).then(({ data: { text } }) => 	{		console.log(text);document.getElementById("leaving").value=text;         	}) 	}	        </script>   <br><br><br>   <center>   <form action="bill_generate.php" method="post">   <table class="design-1">     <tr>       <td align=center colspan=3><h1>Generate bill as a vehicle leaves the lot</h1></td>     </tr>     <tr>       <td width=40%>Enter the license plate of the leaving vehicle</td>       <td align=center><input type=text name="leaving" id="leaving" placeholder="License plate"/></td> 	  	  <td align=center><input type="file" id="myfile" onchange="read_text()"></td>       </tr>       <tr>         <td colspan=3 align=center><input type="submit" name="submit" id="submit" value="Generate bill" class="button"></td>       </tr>     </table> 	</form>  </center>   </body> </html> 
